mapper.update:
UPDATE platform_order
SET status = #{status}
WHERE id = #{orderID}

UPDATE platform_order
SET status = #{status}
WHERE id IN

    #{item}

UPDATE platform_order
SET target = #{target}
WHERE platform_order_id IN

    #{v}

mapper.select:
SELECT id
FROM platform_order
WHERE platform_order_number = #{1}

SELECT * FROM platform_order AS po WHERE po.shop_id IN
#{item}
AND shipping_time >= #{begin} AND #{end} >= shipping_time AND shipping_invoice_number IS NULL

SELECT shipping_time FROM platform_order WHERE shipping_invoice_number IS NULL AND erp_status = 3 AND shop_id IN
#{item}
ORDER BY shipping_time LIMIT 1

SELECT shipping_time FROM platform_order WHERE shipping_invoice_number IS NULL AND erp_status = 3 AND shop_id IN
#{item}
ORDER BY shipping_time DESC LIMIT 1

SELECT *
FROM client_platform_order

    WHERE clientID = #{id}

SELECT *
FROM platform_order po
WHERE DATE_FORMAT(po.order_time, '%c') = #{month}

    AND country = #{country}


    AND logistic_channel_name = #{channelName}

SELECT internal_tracking_number
FROM platform_order
WHERE logistic_channel_name IN

    #{transporter}

  AND shipping_time >= #{startDate}
  AND shipping_time <= #{endDate}
  AND internal_tracking_number NOT IN (SELECT p.bill_code
                                       FROM parcel_trace pt
                                                JOIN parcel p ON pt.parcel_id = p.id
                                       WHERE pt.scan_type = 'End Delivered')
  AND internal_tracking_number IS NOT NULL;

SELECT platform_order_id
FROM platform_order po join shop s ON po.shop_id = s.id
WHERE erp_code IN

    #{shop}

AND order_time >= #{startDateTime}
AND order_time <= #{endDateTime}
AND shipping_invoice_number IS NULL
AND erp_status IN (1,2);

SELECT platform_order_id
FROM platform_order po join shop s ON po.shop_id = s.id
WHERE erp_code NOT IN

    #{shop}

AND shipping_time >= #{startDateTime}
AND shipping_time <= #{endDateTime}
AND shipping_invoice_number IS NOT NULL
AND erp_status = 3;

SELECT po.platform_order_id, po.tracking_number, s.shopify_prefix, s.shopify_token FROM platform_order po join shop s ON po.shop_id = s.id WHERE erp_code IN
#{shop}
AND ready_for_shopify_sync = 1 AND erp_status = 3 ORDER BY shipping_time;

SELECT id
FROM platform_order po
WHERE po.shop_id IN

    #{shop}

AND po.shipping_time between #{startDate} AND #{endDate}
AND po.erp_status = 3;
