mapper.update:
UPDATE purchase_order
SET payment_document = #{fileName}
WHERE id = #{purchaseID}

mapper.select:
SELECT p.*
FROM purchase_order p
WHERE p.client_id = #{clientID}
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM purchase_order p
WHERE p.client_id = #{clientID}

SELECT payment_document
FROM purchase_order
WHERE id = #{purchaseID}
