mapper.select:
SELECT invoice_number
FROM shipping_invoice s
WHERE s.id = #{invoiceID}

SELECT total_amount, discount_amount, final_amount, paid_amount
FROM shipping_invoice s
WHERE s.invoice_number = #{invoiceNumber}

SELECT id, fret_fee, country, order_service_fee
FROM platform_order p
WHERE p.shipping_invoice_number = #{invoiceNumber}

SELECT quantity, service_fee, vat, shipping_fee
FROM platform_order_content p
WHERE p.platform_order_id = #{platformOrderId}
